import React, { Component } from "react";

class Membro extends Component{

    constructor(props){
        super(props);
        this.state = {
            nome: props.nome
        }

        this.entrar = this.entrar.bind(this);
    }

    render(){
        return(
            <div>
                <h2>Bem-vindo(a) {this.state.nome} </h2>
                <button onClick={this.entrar}> 
                    Entrar como Arthur
                </button>
                <button> 
                    Sair
                </button>
            </div>
        );
    }
}

export default Membro;
