import React, { Component } from 'react';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      status : 2
    };
  }

  render(){
    let texto;

    if(this.state.status === 1)
      texto = <h1>teste</h1>
    else
      texto = '';

    return (
      <div>
        { texto }
      </div>
    );
  }
}

export default App;
