import React, { Component } from 'react';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      status : 1
    };
  }

  render(){
    let texto;

    // Pode usar ternário
    // Pode usar a condição dentro das chaves no html

    if(this.state.status === 1)
      texto = <h1>teste</h1>
    else
      texto = '';

    return (
      <div>
        { texto }
        <div>
          <h2>Curso React JS</h2>
        </div>
      </div>
    );
  }
}

export default App;
