import React, { Component } from 'react';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      status : 1,
      status2 : false
    };
  }

  render(){
    let texto;

    // Pode usar ternário
    // Pode usar a condição dentro das chaves no html
    // Ou pode usar como abaixo

    if(this.state.status === 1)
      texto = <h1>Renderização condicional 1</h1>
    else
      texto = '';

    return (
      <div>
        { texto }


        { this.state.status2 ? <h1>Você está logado</h1> : <h1>Faça login, visitante!</h1>}

        <div>
          <h2>Curso React JS</h2>
        </div>
      </div>
    );
  }
}

export default App;
